mapper.insert:

			SELECT domain('sales_chit_no')
			FROM dual

		INSERT INTO sales (
                     sales_chit_no 
           			 ,chit_date 
           			 ,client_code 
           			 ,acct_name 
       			     ,supply_price 
            		 ,vat 
           		  	 ,total_price 
           	 	 	 ,writer 
           			 ,rec_balance 
	       			 ,summary 
           			 ,saleslip_no 
                    )
			  VALUES(
		           #{salesChitNo}
		          ,#{chitDate}
		          ,#{clientCode}
		          ,#{acctName}
		          ,#{supplyPrice}
		          ,#{vat}
	              ,#{totalPrice}
	              ,#{writer}
                  ,0
                  ,#{summary}
                  ,#{saleslipNo}
		        )


		SELECT domain('invoice_no')
		FROM dual

	INSERT INTO invoice(
            invoice_no 
            ,invoice_date 
            ,saleslip_no 
       )
       VALUES(
            #{invoiceNo}
            ,#{chitDate}
            ,#{salesChitNo}
       )

mapper.update:
	    UPDATE invoice
        SET invoice_status = #{state}
            ,nts_invoice_date = CASE WHEN #{state} ='f2' THEN NULL
            						 ELSE SYSDATE
            						 END
        WHERE invoice_no = #{invoiceNo}

mapper.insert:

		SELECT NVL(MAX(log_id),0) + 1
		FROM receivables_log

	INSERT INTO  receivables_log(
            log_id  
            ,rec_date  
            ,increase_price  
            ,client_code   
            ,sales_chit_no  

        ) VALUES(
           #{logId}
           ,#{chitDate}
           ,#{totalPrice}
           ,#{clientCode}
           ,#{salesChitNo}
           )

mapper.update:
    	UPDATE client
		SET balancek = balancek + #{balancek}
		WHERE client_code = #{clientCode}

UPDATE  sales_chit
SET slip_state = #{state}
WHERE saleslip_no = #{saleslipNo}

mapper.insert:

	SELECT domain('purchase_chit_no')
	FROM dual

INSERT INTO purchase(
        purchase_chit_no 
        ,chit_date 
        ,client_code 
        ,acct_name 
        ,supply_price 
        ,vat 
        ,total_price 
        ,writer 
        ,decrease_balance 
        ,summary 
        ,purcslip_no 
    )
    VALUES(
        #{purchaseChitNo}
        ,#{chitDate}
        ,#{clientCode}
        ,#{acctName}
        ,#{supplyPrice}
        ,#{vat}
        ,#{totalPrice}
        ,#{writer}
        ,0
        ,#{summary}
        ,#{purcslipNo}
    )


		SELECT NVL(MAX(rec_log_id),0) + 1
		FROM payables_log

	INSERT INTO payables_log(
            rec_log_id  
            ,rec_date 
            ,decrease_price  
            ,client_code  
            ,purchase_chit_no 
        ) VALUES(
           #{recLogId}
           ,#{chitDate}
           ,#{totalPrice}
           ,#{clientCode}
           ,#{purchaseChitNo}
       )

mapper.update:
    	UPDATE client
		SET balancem = balancem + #{balancem}
		WHERE client_code = #{clientCode}

UPDATE  purchase_chit
SET slip_state = 'r2'
WHERE purcslip_no = #{purcslipNo}

UPDATE sales
SET invoice_no = #{invoiceNo}
WHERE sales_chit_no = #{salesChitNo}

			UPDATE sales
			SET      chit_date  = #{chitDate}
           			 ,client_code = #{clientCode}
           			 ,acct_name  = #{acctName}
       			     ,supply_price = #{supplyPrice}
            		 ,vat = #{vat}
           		  	 ,total_price = #{totalPrice}
           	 	 	 ,writer = #{writer}
	       			 ,summary = #{summary}
           			 ,saleslip_no = #{saleslipNo}
			WHERE sales_chit_no = #{salesChitNo}
