mapper.update:
UPDATE employees_detail
SET  

	phone = #{phone}


	,tel = #{tel}


	,email = #{email}


	,employee_image = #{employeeImage}


	,zipcode = TO_NUMBER(#{zipcode})


	,detail_address = #{detailAddress}


	,address = #{address}

WHERE employee_Code = #{employeeCode}

mapper.select:
SELECT employee_image
FROM   employees_detail
WHERE  employee_code = #{employeeCode}
